import React, {useState} from 'react';
import {Button, Card, Modal} from "react-bootstrap";
import {Document,Page} from "react-pdf"
import hm from "../sample.pdf"


const License = ({license}) => {
    function LicenseModal(props) {
    return (
        <Modal
            {...props}
            size="lg"
            aria-labelledby="contained-modal-title-vcenter"
            centered
            className={'beat-item'}
        >
            <Modal.Header closeButton>
                <div className="beate-info text-lg-center">
                    {/*<h2>{props.license.license_type}</h2>*/}
                    <h2 className="text-center">MP3 License Info</h2>
                </div>
            </Modal.Header>
            <Modal.Body className={"license-modal-body"}>
                <div className="pdf-body">
                    <div>
                        <i className="fas fa-microphone-alt"/> &nbsp;
                        <span>
                            Used For Music Recording
                        </span>
                    </div>
                    <div>
                        <i className="fas fa-layer-group"/> &nbsp;
                        <span>
                            Distribute up to 25000 copies
                        </span>
                    </div>
                    <div style={{ position: "relative",
                                  right: "0.3em"}}>
                        <i className="fas fa-wifi"/> &nbsp;
                        <span>
                            25000 Online Audio Streams
                        </span>
                    </div>
                    <div>
                        <i className="fas fa-video"/> &nbsp;
                        <span>
                            0 Music Videos
                        </span>
                    </div>
                    <div>
                        <i className="fas fa-microphone"/> &nbsp;
                        <span>
                    Non-profit Live Performances only
                </span>
                    </div>
                    <div>
                        <i className="fas fa-broadcast-tower"/> &nbsp;
                        <span>
                    No Radio Broadcasting rights
                </span>
                    </div>
                </div>
                {/*/!*<Document file={props.license.license_content}>*!/*/}
                {/*<Document file={hm}>*/}
                {/*        <Page pageNumber={1} />*/}
                {/*</Document>*/}
            </Modal.Body>
        </Modal>
    );
}
    const [modalShow, setModalShow] = useState(false);

    return (
        <>
            <Card >
                <Card.Header >
                    <div className={"text-center card-info text-nowrap"} style={{paddingTop:"5em"}}>
                            <span className={"license-name d-block"}>
                                {license.license_type}
                            </span>
                            <span className={"license-price d-block font-weight-bold"}>
                                ${license.license_price}
                            </span>
                            <span className={'text-uppercase font-weight-bold d-block'}>
                                per unit
                            </span>
                        </div>
                    <br/>
                </Card.Header>
                <Card.Body>
                        <div className={"license-button"}>
                            <Button onClick={()=>setModalShow(true)}>Read full license</Button>
                        </div>
                    </Card.Body>
            </Card>
            <LicenseModal
                show={modalShow}
                onHide={() => setModalShow(false)}
                license = {license}
            />
        </>
    );

}

export default License;
