import React, {useEffect, useState} from 'react';
import BeatList from "../components/BeatList";
import {apiGetBeats, apiRequests} from "../backendQuery";
import Navigation from "../components/Navigation"
import {InputGroup, FormControl, Button, Table, Form, Card, Container, Row, Col, ButtonGroup,CardDeck} from "react-bootstrap"
import Player from "../components/AudioPlayer";
import Footer from "../components/Footer";


const IndexPage = () => {
    const [beat, setBeat] = useState("")
    // const user = localStorage.getItem('username');
    const handleReq = (data) =>{
        console.log(data)
    }
    const selectedBeat =(beat)=>{
        setBeat(beat);
    }

    return (
        <div id={"main-content"}>
            <Navigation/>
            <section>
                <div className="page-top main-bg">
                    <div className="headliner">
                     <div className="input-box mx-auto">
                         <Form>
                             <div id="main-search" className={"d-flex justify-content-center"} >
                                 <InputGroup>
                                     <FormControl
                                         type="search"
                                         placeholder="What are you looking for?"
                                         aria-label="beats-query-search"
                                         aria-describedby="basic-addon2"
                                     />
                                 </InputGroup>
                                 <button>
                                     <i className="fa fa-search">

                                     </i>
                                 </button>
                             </div>
                         </Form>
                     </div>
                 </div>
                </div>
            </section>
            <section className="beatlist">
                
                    <BeatList selectedBeat={selectedBeat}/>

                    <div className={'show-more'}>
                        <Button className={'mt-5 mb-5 button-2'}>
                            Browse All Tracks
                        </Button>
                    </div>

            </section>
                {/*<Player beat={beat} />*/}

            <Footer/>
        </div>
    );
}

export default IndexPage;
