import React, {useEffect, useState} from 'react';
import {apiGetBeats} from "../backendQuery";
import Beat from "./Beat";
import {Button, ButtonGroup, Table} from "react-bootstrap";

const BeatList = ({beats,selectedBeat}) => {
    const [play, setPlay] = React.useState(false);

    const handleClick = (beat) => {
        setPlay(true)
        // localStorage.setItem('beat', (beat.id).toString())
        // window.location.replace(`beat/${beat.id}`)

    }
    // const [beats, setBeats] = useState([])
    // useEffect(() => {
    //     const handleBeatsList = (response) => {
    //         setBeats(response.data);
    //     }
    //     apiGetBeats(handleBeatsList)
    // },[])

    return (
        <>
            <Table className={'text-center'} hover >
                <thead>
                <tr>
                    <th>Title</th>
                    <th>Length</th>
                    <th>BPM</th>
                    <th>Genre</th>
                    <th>Options</th>
                </tr>
                </thead>
                <tbody className={'font-weight-bold'}>
                {beats.map((beat) => (
                    <tr key={beat.id} className={"h5"} onClick={()=>selectedBeat(beat)}>
                        <Beat key={beat.id} beat={beat} selectedBeat={selectedBeat}/>
                    </tr>
                ))}
                </tbody>
            </Table>


        </>
    );
}

export default BeatList;
