import {backendLookup, backendAuthentication} from "./lookup/index";


export function apiAuthenticateUser(callback, username, password) {
    backendAuthentication("POST", `http://127.0.0.1:8000/api/client/auth/`, callback,
        {
            "username": username,
            "password": password,
        }
    )
}

export function apiGetBeats(callback) {
    backendLookup("GET", `http://127.0.0.1:8000/api/client/beats/`, callback,)
}


export function apiGetShyneRequest(callback) {
    backendLookup("GET", "/shyne-request/", callback)
}


export function apiGetLocation(clientId, callback) {
    backendLookup("GET", `http://127.0.0.1:8000/api/client/locations/${clientId}/`, callback)
}

export function apiRequests(callback, token) {
    backendLookup("GET", `http://127.0.0.1:8000/api/client/orders/`, callback,
        {"token": token})
}

export function apiGetRequests(callback, token) {
    backendLookup("GET", `http://127.0.0.1:8000/api/client/orders/`, callback,
        {"token": token})
}
