import React, {useState} from 'react';
import {Button, Col, Container, Form, Row} from "react-bootstrap";
import Navigation from "../components/Navigation";
import axios from "axios";

const UploadPage = () => {
    const [title, setTitle] = useState('');
    const [mp3, setMp3] = useState('');
    const [wav, setWav] = useState('');
    const [stems, setStems] = useState('');

    const onSubmit = e => {
        e.preventDefault();

        const beat = {
            title:title ,
            mp3: mp3,
            wav: wav,
            stems: stems
        };
        axios.post('/api/client/beats/', {beat})
        // console.log(beat)
    }

    return (
        <>
            <Navigation/>
            <div className="upload-div">
                <h2 className={'text-center font-weight-bold'}>Upload Beats Here</h2>
                <div className="upload-form m-5">
                    <Container>
                        <Form onSubmit={onSubmit}>
                            <Form.Row>
                                <Form.Group as={Col}>
                                    <Form.Label>Beat Title</Form.Label>
                                    <Form.Control type="text" id={'title'} onChange={e => setTitle(e.target.value) } className={'upload-form-input'}/>
                                </Form.Group>
                            </Form.Row>
                            <Form.Row>
                                <Form.Group as={Col}>
                                    <Form.Label>Mp3 File</Form.Label>
                                    <Form.Control type={"file"} id={'mp3_file'} onChange={e => setMp3(e.target.value) } className={'upload-form-input rounded'}/>
                                </Form.Group>
                                <Form.Group as={Col}>
                                    <Form.Label>Wav File</Form.Label>
                                    <Form.Control type="file"  id={'wav_file'} onChange={e => setWav(e.target.value) } className={'upload-form-input rounded'}/>
                                </Form.Group>

                            </Form.Row>
                            <Form.Row>
                                <Form.Group as={Col}>
                                    <small className={'mx-auto'}>Please remember to upload your stem tracks as a zip file.</small>
                                    <br/>
                                    <Form.Label>Stem Tracks</Form.Label>
                                    <Form.Control type="file" id={'stems'} onChange={e => setStems(e.target.value) } className={'upload-form-input rounded'}/>
                                </Form.Group>
                            </Form.Row>
                            <Row>
                                <Button className={"button-2 mx-auto mb-5"} type={"submit"}>Upload File </Button>
                            </Row>
                        </Form>
                    </Container>
                </div>
            </div>
        </>
    );
}

export default UploadPage;
