import React, {useEffect, useState} from 'react';
import {apiGetBeats} from "../backendQuery";
import Beat from "./Beat";
import {Button, ButtonGroup, Table} from "react-bootstrap";

const BeatList = () => {
    const [beats, setBeats] = useState([])
    useEffect(() => {
        const handleBeatsList = (response) => {
            setBeats(response.data);
        }
        apiGetBeats(handleBeatsList)
    },[])

    return (
        <>
            <Table className={'text-center'} hover >
                <thead>
                <tr>
                    <th>Title</th>
                    <th>Length</th>
                    <th>BPM</th>
                    <th>Genre</th>
                    <th>Options</th>
                </tr>
                </thead>
                <tbody className={'font-weight-bold'}>
                {beats.map((beat) => (
                    <tr className={"h5"}>
                        <td>
                            <span>
                                <img src="marv.png" alt=""/>
                            </span>
                            <span>
                               &nbsp; {beat.title}
                            </span>
                        </td>
                        <td>02:54</td>
                        <td> 0 </td>
                        <td>
                            <span className={"beat-labels modal-beat-label"}>HipHop</span>
                        </td>
                        <td>
                            <ButtonGroup>
                                <Button className={'mr-3'}>Add to Cart</Button>
                                <Button >Share</Button>
                            </ButtonGroup>
                        </td>

                    </tr>


                    // <Beat key={beat.id} beat={beat}/>
                ))}
                </tbody>
            </Table>


        </>
    );
}

export default BeatList;
