import React, {useState} from 'react';
import {CartProvider, useCart} from "react-use-cart";
import {Button, Col, Container, Form, Row, Table} from "react-bootstrap";
import Navigation from "./Navigation";
import axios from "axios";

const Cart = () => {
    const { items,totalUniqueItems,cartTotal,removeItem } = useCart();
    const mat = () => {

    }
    // apiGetShyneRequest(mat, {items})
    function getCookie(name) {
        var cookieValue = null;
        if (document.cookie && document.cookie !== '') {
            var cookies = document.cookie.split(';');
            for (var i = 0; i < cookies.length; i++) {
                var cookie = cookies[i].trim();
                // Does this cookie string begin with the name we want?
                if (cookie.substring(0, name.length + 1) === (name + '=')) {
                    cookieValue = decodeURIComponent(cookie.substring(name.length + 1));
                    break;
                }
            }
        }
        return cookieValue;
    }

    const [email, setEmail] = useState('');
    const [firstName, setFirstName] = useState('');
    const [lastName, setLastName] = useState('');
    const csrftoken = getCookie('csrftoken');

    function csrfSafeMethod(method) {
        return (/^(GET|HEAD|OPTIONS|TRACE)$/.test(method))
    }
    const onSubmit = e => {
        e.preventDefault();

        const user = {
            email: email,
            firstName: firstName,
            lastName: lastName
        };

        // axios.post('/api/client/manage-payment/', {
        //     firstName: 'Fred',
        //     lastName: 'Flintstone'
        // }).then(function (response) {
        //     console.log(response);
        // })
        axios({
            method: 'post',
            url: '/api/client/manage-payment/',
            data: {
                firstName: 'Fred',
                lastName: 'Flintstone'
            },
            headers: { 'xsrfCookieName': 'csrftoken', 'xsrfHeaderName': 'X-CSRFToken'},
        });
    }
       return (
           <>
               <Navigation/>
               <Container>
                   <div className="mt-5">
                        <h2 className={'text-center font-weight-bold'}>Confirm Your Order</h2>
                   </div>
                   <Table className={'text-center'} hover >
                       <thead>
                       <tr>
                           <th>Cart Item</th>
                           <th>License Type</th>
                           <th>Price</th>
                           <th>Options</th>
                       </tr>
                       </thead>
                       <tbody className={'font-weight-bold'}>
                       {items.map((item) => (
                           <tr key={item.id} className={"h5"}>
                               <td>{item.name}</td>
                               <td>{item.license}</td>
                               <td>{item.price}</td>
                               <td>
                                   <button className={"button-1"} onClick={() => removeItem(item.id)}>Remove Item &times;</button>
                               </td>
                           </tr>
                       ))}
                       </tbody>
                   </Table>
               </Container>
               <div className="checkout-div mt-5">
                        <h2 className={'text-center font-weight-bold'}>Fill this out to complete your order</h2>
                        <div className="contact-form m-5">
                            <Container>
                                <Form onSubmit={onSubmit}>
                                    <Form.Row>
                                        <Form.Group as={Col}>
                                            <Form.Label>First Name</Form.Label>
                                            <Form.Control type="text" id={"first_name"}
                                                          className={'checkout-form-input'}
                                                          onChange={e => setFirstName(e.target.value)}

                                            />
                                        </Form.Group>
                                        <Form.Group as={Col}>
                                            <Form.Label>Last Name</Form.Label>
                                            <Form.Control type="text" className={'checkout-form-input'}
                                                          onChange={e => setLastName(e.target.value)}

                                            />
                                        </Form.Group>
                                    </Form.Row>
                                    <Form.Row>
                                        <Form.Group as={Col}>
                                            <Form.Label>Email</Form.Label>
                                            <Form.Control type={"email"} className={'checkout-form-input'}
                                                          onChange={e => setEmail(e.target.value)}
                                            />
                                        </Form.Group>
                                    </Form.Row>

                                    <Row>
                                        <Button className={"button-2 mx-auto mb-5"} type={"submit"}>Proceed To Checkout</Button>
                                    </Row>
                                </Form>
                            </Container>
                        </div>
                    </div>
           </>

    )
}

export default Cart;
