import React, {useEffect, useState} from 'react';
import {apiGetLicense} from "../backendQuery";
import { Document } from 'react-pdf';
import {Button, Card, CardDeck, Carousel, Container} from "react-bootstrap";
import License from "./License";

const LicenseList = ({selectedLicense}) => {
    const [licenses, setLicenses] = useState([])
    // Carousel
    const [index, setIndex] = useState(0);
    const ass = [
        {
            "license_type":"MP# License",
            "license_price": "80"
        },
        ]
    const handleSelect = (selectedIndex, e) => {
        setIndex(selectedIndex);
    }
    setLicenses(ass)

    useEffect(() => {
        const handleBeatsList = (response) => {
            // setLicenses(response.data);
            setLicenses(ass);
        }
        // apiGetLicense(handleBeatsList)
    },[])

    return (
        <>
            <Container>
                <CardDeck>
                    {licenses.map((license)=> (
                        <p>{license}</p>
                    ))}
                        </CardDeck>
                <Carousel activeIndex={index} onSelect={handleSelect} controls={false} indicators={true} interval={7000}>
                    {licenses.map((license)=> (
                    <Carousel.Item>
                        <Card>
                            <Card.Header >
                                <div className={"text-center card-info"}>
                                <span className={"license-name d-block"}>
                                    {license.license_type}
                                </span>
                                    <span className={"license-price d-block font-weight-bold"}>
                                    ${license.license_price}
                                </span>
                                    <span className={'text-uppercase font-weight-bold d-block'}>
                                    per unit
                                </span>
                                </div>
                                <br/>
                            </Card.Header>
                            <Card.Body>
                                <div className={"license-button"}>
                                    {/*<Button className={""} onClick={()=>handleClick(license)}>Read full license</Button>*/}
                                </div>
                            </Card.Body>
                        </Card>
                    </Carousel.Item>
                        ))}
                </Carousel>
            </Container>

        </>
    );

}

export default LicenseList;
