import React, {useState} from 'react';
import {Button, Col, Container, Form, Row} from "react-bootstrap";
import Navigation from "../components/Navigation";
import axios from "axios";

const UploadPage = () => {
    const [email, setEmail] = useState('');
    const [firstName, setFirstName] = useState('');
    const [lastName, setLastName] = useState('');

    const onSubmit = e => {
        e.preventDefault();

        const user = {
            email: email,
            firstName: firstName,
            lastName: lastName
        };
        axios.post('/api/client/beats/')
    }

    return (
        <>
            <Navigation/>
            <div className="upload-div">
                <h2 className={'text-center font-weight-bold'}>Upload Beats Here</h2>
                <div className="contact-form m-5">
                    <Container>
                        <Form>
                            <Form.Row>
                                <Form.Group as={Col}>
                                    <Form.Label>Beat Title</Form.Label>
                                    <Form.Control type="text" value={'title'} className={'upload-form-input'}/>
                                </Form.Group>
                            </Form.Row>
                            <Form.Row>
                                <Form.Group as={Col}>
                                    <Form.Label>Mp3 File</Form.Label>
                                    <Form.Control type={"file"} value={'mp3_file'} className={'upload-form-input rounded'}/>
                                </Form.Group>
                                <Form.Group as={Col}>
                                    <Form.Label>Wav File</Form.Label>
                                    <Form.Control type="file"  value={'wav_file'} className={'upload-form-input rounded'}/>
                                </Form.Group>

                            </Form.Row>
                            <Form.Row>
                                <Form.Group as={Col}>
                                    <small className={'mx-auto'}>Please remember to upload your stem tracks as a zip file.</small>
                                    <br/>
                                    <Form.Label>Stem Tracks</Form.Label>
                                    <Form.Control type="file" value={'stem_tracks'} className={'upload-form-input rounded'}/>
                                </Form.Group>
                            </Form.Row>
                            <Row>
                                <Button className={"button-2 mx-auto mb-5"} type={"submit"}>Upload File </Button>
                            </Row>
                        </Form>
                    </Container>
                </div>
            </div>
        </>
    );
}

export default UploadPage;
