import React, {useState} from 'react';
import {CartProvider, useCart} from "react-use-cart";
import {apiGetShyneRequest} from "../backendQuery";
import {Button, Col, Container, Form, Row, Table} from "react-bootstrap";
import Navigation from "./Navigation";
import axios from "axios";

const Cart = () => {
    const { items,totalUniqueItems,cartTotal } = useCart();
    const mat = () => {

    }
    apiGetShyneRequest(mat, {items})

    const [email, setEmail] = useState('');
    const [firstName, setFirstName] = useState('');
    const [lastName, setLastName] = useState('');

    const onSubmit = e => {
        e.preventDefault();

        const user = {
            email: email,
            firstName: firstName,
            lastName: lastName
        };

        axios.post('/api/client/manage-checkout/',user).then(function (response) {
            console.log(response);
        })
    }
       return (
           <>
                <Navigation cartTotal={cartTotal} />
                <div className="checkout-div mt-5">
                        <h2 className={'text-center font-weight-bold'}>Fill this out to complete your order</h2>
                        <div className="contact-form m-5">
                            <Container>
                                <Form>
                                    <Form.Row>
                                        <Form.Group as={Col}>
                                            <Form.Label>First Name</Form.Label>
                                            <Form.Control type="text" id={"first_name"} className={'checkout-form-input'}/>
                                        </Form.Group>
                                        <Form.Group as={Col}>
                                            <Form.Label>Last Name</Form.Label>
                                            <Form.Control type="text" className={'checkout-form-input'}/>
                                        </Form.Group>
                                    </Form.Row>
                                    <Form.Row>
                                        <Form.Group as={Col}>
                                            <Form.Label>Email</Form.Label>
                                            <Form.Control type={"email"} className={'checkout-form-input'}/>
                                        </Form.Group>
                                    </Form.Row>

                                    <Row>
                                        <Button className={"button-2 mx-auto mb-5"} type={"submit"}>Send Message</Button>
                                    </Row>
                                </Form>
                            </Container>
                        </div>
                    </div>
               <Table className={'text-center'} hover >
                   <thead>
                   <tr>
                       <th>Cart Item</th>
                       <th>License Type</th>
                       <th>Price</th>
                       <th>Options</th>
                   </tr>
                   </thead>
                   <tbody className={'font-weight-bold'}>
                   {items.map((item) => (
                       <tr key={item.id} className={"h5"}>
                            <td>{item.name}</td>
                            <td>{item.license}</td>
                            <td>{item.price}</td>
                            {/*<td>{item.}</td>*/}
                       </tr>
                   ))}
                   </tbody>
               </Table>

           </>

    )
}

export default Cart;
